void showMario(String bitrgbstr, int xlength, int ylength, int x, int y, uint16_t colorxy) {
  //Serial.println("bitrgbstr = " + bitrgbstr);
  for (int i = x; i < x+(xlength); i = i + (1)) {
    for(int j = y; j < y+(ylength); j = j + (1)){
      if (String(bitrgbstr.charAt(((j-y)*xlength+i-x))).toInt() != 0) {
        display.drawPixel(i,j,colorxy);
      } else {
        display.drawPixel(i,j,display.color565( 230, 158, 33));
      }
    }
  }
  }


void show_mario_number(int number, int xlength, int ylength, int x, int y, uint16_t colorxy){
  String numStr = String(number);
  if(number<10){
    showMario(bitdata12[(int)(0)],xlength,ylength, x, y, colorxy);
    showMario(bitdata12[(int)((String(numStr.charAt(0)).toInt() + 1) - 1)],xlength,ylength, x+9, y, colorxy);
  }else if(number<100){
    showMario(bitdata12[(int)((String(numStr.charAt(0)).toInt() + 1) - 1)],xlength,ylength, x, y, colorxy);
    showMario(bitdata12[(int)((String(numStr.charAt(1)).toInt() + 1) - 1)],xlength,ylength, x+9, y, colorxy);
  }
}
